package javaPractice;

public class IncrementalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		//post increment 
		
		int b= a++;
		System.out.println(a);
		System.out.println(b);
		
		// pre increment 
		
		int a1=10;
		int b1=++a1;
		System.out.println(a1);
		System.out.println(b1);
				
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
